mapper.update:
UPDATE ARTICLE
SET view_cnt = view_cnt + #{viewCount}
WHERE article_id = #{articleId}

mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM ARTICLE
    WHERE article_id = #{articleId}
    AND writer = #{userId}
)

mapper.update:
UPDATE ARTICLE
SET title = #{title},
    content = #{content},
    category_code = #{categoryCode}
WHERE article_id = #{articleId}

mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM ARTICLE
    WHERE article_id = #{articleId}
)

mapper.update:
UPDATE ARTICLE
SET like_cnt = (
    SELECT COUNT(*) FROM ARTICLE_LIKE WHERE article_id = #{articleId}
)
WHERE article_id = #{articleId}

mapper.select:
SELECT writer
FROM ARTICLE
WHERE article_id = #{articleId}

mapper.update:
UPDATE ARTICLE
SET comment_count = comment_count + 1
WHERE article_id = #{articleId}

UPDATE ARTICLE
SET comment_count = CASE WHEN comment_count > 0 THEN comment_count - 1 ELSE 0 END
WHERE article_id = #{articleId}

UPDATE ARTICLE
SET media_type = #{mediaType}
WHERE article_id = #{articleId}

UPDATE ARTICLE
SET status = 'BLIND'
WHERE article_id = #{articleId}
