mapper.select:
SELECT EXISTS (
    SELECT 1
    FROM COMMENT
    WHERE comment_id = #{commentId}
    AND writer = #{writer}
)

SELECT EXISTS (
    SELECT 1
    FROM COMMENT
    WHERE comment_id = #{commentId}
)

mapper.update:
UPDATE COMMENT
SET content = #{content}
WHERE comment_id = #{commentId}

UPDATE COMMENT
SET like_cnt = (
    SELECT COUNT(*) FROM COMMENT_LIKE WHERE comment_id = #{commentId}
)
WHERE comment_id = #{commentId};

mapper.select:
SELECT writer
FROM COMMENT
WHERE comment_id = #{commentId}
